import React, {Component} from 'react';
import Navigation from './components/Navigation/Navigation';
import Signin from './components/Signin/Signin.js';
import Register from './components/Register/Register.js';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js';
import Rank from './components/Rank/Rank';
import ParticlesBg from 'particles-bg';
import './App.css';


const returnClarifiRequestOptions = (imageUrl) => {
  // Your PAT (Personal Access Token) can be found in the Account's Security section
const PAT = 'daa936fb4067482cb04eb292c5269f03';
// Specify the correct user_id/app_id pairings
// Since you're making inferences outside your app's scope
const USER_ID = 'acc5n45nn80u';       
const APP_ID = 'FaceDetection';
// Change these to whatever model and image URL you want to use
const MODEL_ID = 'face-detection';   
const IMAGE_URL = imageUrl;

const raw = JSON.stringify({
  "user_app_id": {
      "user_id": USER_ID,
      "app_id": APP_ID
  },
  "inputs": [
      {
          "data": {
              "image": {
                  "url": IMAGE_URL
              }
          }
      }
  ]
});
const requestOptions = {
  method: 'POST',
  headers: {
      'Accept': 'application/json',
      'Authorization': 'Key ' + PAT
  },
  body: raw
};

return requestOptions;
}


class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin',
      isSignedIn: false,
      user: {
        id: '',
        name: '',
        email:'',
        entires: 0, //time submitted the photos
        joined: ''
      }
    }
  }

  // componentDidMount() {
  //   fetch('http://localhost:3000/')
  //     .then(response => response.json())
  //     .then(console.log)
  // }


  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    }
  }

  displayFaceBox = (box) => {
    //console.log(box)
    this.setState({box: box})
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value});
  }

  // onButtonSubmit = () => {
  //   this.setState({imageUrl: this.state.input});
  //   //app.models.predict('face-detection', this.state.input)
  //   fetch("https://api.clarifai.com/v2/models/" + 'face-detection' +  "/outputs", returnClarifiRequestOptions(this.state.input))
  //       .then(response => response.json())
  //         .then(response => {
  //           console.log('hi', response)
  //           if (response) {
  //             fetch('http://localhost:3000/image', {
  //               method: 'put',
  //               headers: {'Content-Type':'application/json'},
  //               body: JSON.stringify({
  //                 id: this.state.user.id
  //               })
  //             })
  //           }
  //           this.displayFaceBox(this.calculateFaceLocation(response))
  //         })
  //         .catch(err => console.log(err));

  // }
  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input});
    fetch("https://api.clarifai.com/v2/models/" + 'face-detection' +  "/outputs", returnClarifiRequestOptions(this.state.input))
      .then(response => response.json())
      .then(response => {
        console.log('hi', response)
        if (response) {
          this.displayFaceBox(this.calculateFaceLocation(response));
        } else {
          console.error('Clarifai API error:', response.status.description);
        }
      })
      .catch(err => console.log(err));
  }

  onRouteChange = (route) => {
    if(route === 'signout') {
      this.setState({isSignedIn: false})
    } 
    else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route})
  }



  render() {
    return (
      <div className="App">
        <ParticlesBg type="polygon" bg={true} />
        <Navigation isSignedIn={this.state.isSignedIn} onRouteChange={this.onRouteChange}/>
        { this.state.route === 'home'
            ? <div>
                <Logo />
                <Rank />
                <ImageLinkForm 
                  onInputChange={this.onInputChange} 
                  onButtonSubmit={this.onButtonSubmit}
                />
                <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>
              </div>
            : (
                this.state.route === 'signin' 
                ? <Signin onRouteChange={this.onRouteChange}/>
                : <Register onRouteChange={this.onRouteChange}/>
              )

       }
      </div>
    );
  }
}

export default App;
